import numpy as np

# Define the vertices of the triangle
A = np.array([0, 3])
B = np.array([0, 0])
C = np.array([4, 0])

# Calculate the centroid G
G = (A + B + C) / 3

# Define the projection function
def project_point_to_line(P, line_point1, line_point2):
    line_vector = line_point2 - line_point1
    point_vector = P - line_point1
    projection = line_point1 + np.dot(point_vector, line_vector) / np.dot(line_vector, line_vector) * line_vector
    return projection

# Calculate the projections of G onto the sides BC, AC, and AB
P = project_point_to_line(G, B, C)
Q = project_point_to_line(G, A, C)
R = project_point_to_line(G, A, B)

# Calculate the distances GP, GQ, and GR
GP = np.linalg.norm(G - P)
GQ = np.linalg.norm(G - Q)
GR = np.linalg.norm(G - R)

# Calculate the sum of the distances
result = GP + GQ + GR
print(result)
