A=(0,.866);
B=(.5,1.732);
C=(1.5,1.732);
D=(2,.866);
E=(1.5,0);
F=(.5,0);
G=(.5,1);
H=(1.5,1);
draw(A--B);
draw(B--C);
draw(C--D);
draw(D--E);
draw(E--F);
draw(F--A);
draw(F--G);
draw(G--H);
draw(H--E);
