[asy]

draw((0,0)--(12,0)--(12,5)--(8,5)--(8,4)--(5,4)
--(5,6)--(0,6)--(0,0));

label("6",(0,3),W);
label("5",(2.5,6),N);
label("2",(5,5),W);
label("3",(6.5,4),S);
label("1",(8,4.5),E);
label("4",(10,5),N);

[/asy]

